import PropTypes from "prop-types";
import * as BooksApi from "../utils/BooksAPI"
import {useEffect, useState} from "react";
import Book from "./Book";

const SearchBar = ({toggleShowSearchPage}) => {
    const [query, setQuery] = useState("")
    const [searchedBooks, setSearchedBooks] = useState([]);

    const updateQuery = (query) => {
        setQuery(query.trim())
    }

    useEffect(() => {
        if (query !== "") {
            searchBooks(query)
        }
    }, [query])

    const searchBooks = async (query) => {
        let response = await BooksApi.search(query, 10)
        setSearchedBooks(response)
        console.log(searchedBooks)
    };

    return (
        <div className="search-books">
            <div className="search-books-bar">
                <a
                    className="close-search"
                    onClick={() => toggleShowSearchPage()}
                >
                    Close
                </a>
                <div className="search-books-input-wrapper">
                    <input
                        type="text"
                        placeholder="Search by title, author, or ISBN"
                        onChange={(event) => updateQuery(event.target.value)}
                    />
                </div>
            </div>
            <div className="search-books-results">
                <ol className="books-grid">
                    {searchBooks.length > 0 ?
                    searchedBooks.map((book) => (
                        <Book book={book}/>
                    )):<div>There are no books</div>}
                </ol>
            </div>
        </div>
    )
};

SearchBar.propTypes = {
    toggleShowSearchPage: PropTypes.func.isRequired,
}

export default SearchBar;
