import "./App.css";
import {useEffect, useState} from "react";
import SearchBar from "./components/SearchBar";
import ListBooks from "./components/ListBooks";
import * as BooksApi from "./utils/BooksAPI"


function App() {
    const [showSearchPage, setShowSearchPage] = useState(false);

    const toggleShowSearchPage = () => {
        setShowSearchPage(!showSearchPage)
    }

    // this will be changed with useEffect to load diretcly from the api. For now is harcoded.
    const [shelves, setShelves] = useState([
        {
            id: "currentlyReading",
            name: "Currently Read"
        },
        {
            id: "wantToRead",
            name: "Want to Read"
        }, {
            id: "read",
            name: "Read"
        }]);

    return (
        <div className="app">
            {showSearchPage ? (
                <SearchBar toggleShowSearchPage={toggleShowSearchPage}/>
            ) : (
                <ListBooks shelves={shelves} toggleShowSearchPage={toggleShowSearchPage} books={books}/>
            )}
        </div>
    );
}

export default App;
