import Book from "./Book";
import PropTypes from "prop-types";
import Spinner from "./Spinner";

const BookList = ({books, shelf, isLoading, updateBooks}) => {
        const getBooksInThisShelf = () => {
            return books.map((book) => {
                book.shelf === (shelf && shelf.id) && book
            })
        }

        const booksInThisShelf = getBooksInThisShelf()
        console.log(booksInThisShelf)

        return (
            <ol className="books-grid">
                {
                    isLoading ?
                        <Spinner/>
                        :
                        booksInThisShelf.map((book, index) => (
                            <li key={index}>
                                <Book book={book} updateBooks={updateBooks}/>
                            </li>
                        ))}

            </ol>
        )
    }
;

BookList.propTypes = {
    books: PropTypes.array.isRequired,
    shelf: PropTypes.object,
    isLoading: PropTypes.bool.isRequired,
}

export default BookList;
